import styled from "styled-components";

export const Form = styled.form`
width:25%;
margin:0 auto;
border: 2px solid #000;
padding:20px 15px;

h1 {
    color: #437E90;
    font-family: Roboto;
    font-size: 50px;
    font-weight: 100;
    margin-top:30px;
  }
h3{
    margin:25px 0px;
}
.data{
     margin:70px 0px;
     display: flex;
     flex-direction: column;
     justify-content: space-around;
 }
input{
    border: 1px solid #DAE0E3;
    padding:10px 15px;
    border-radius:5px;
    margin:10px 0px;
}
button{
    border: 1px solid #DAE0E3;
}

`
