import styled from "styled-components";

export const Form = styled.form`
width:25%;
margin:0 auto;
border: 2px solid #000;
padding:20px 15px;
display: flex;
flex-direction: column;
justify-content: space-around;



h1 {
    color: #437E90;
    font-family: Roboto;
    font-size: 50px;
    font-weight: 600;
    line-height: 24px;


  }
input{
    border: 2px solid #DAE0E3;
    padding:10px 15px;
    border-radius:5px;
}

`
